import{_ as a}from"./chunks/grupos-e-subcategorias.CIp_CnkO.js";import{_ as t,c as o,ag as r,o as s}from"./chunks/framework.-a8jReyC.js";const m=JSON.parse('{"title":"Grupos e Categorias","description":"","frontmatter":{"title":"Grupos e Categorias"},"headers":[],"relativePath":"features/groups-and-categories.md","filePath":"features/groups-and-categories.md"}'),i={name:"features/groups-and-categories.md"};function l(n,e,d,u,p,g){return s(),o("div",null,e[0]||(e[0]=[r('<h1 id="grupos-e-categorias" tabindex="-1">Grupos e Categorias <a class="header-anchor" href="#grupos-e-categorias" aria-label="Permalink to &quot;Grupos e Categorias&quot;">​</a></h1><img src="'+a+'" width="600px" alt="Exemplo de grupos e categorias"><p>O FlyCash oferece uma maneira inteligente e flexível de organizar suas finanças, permitindo que você visualize claramente a origem e o destino do seu dinheiro.</p><h3 id="o-que-sao-grupos" tabindex="-1"><strong>O que são Grupos?</strong> <a class="header-anchor" href="#o-que-sao-grupos" aria-label="Permalink to &quot;**O que são Grupos?**&quot;">​</a></h3><p>Grupos são grandes divisões que representam as <strong>fontes de entrada e saída de dinheiro</strong>. Isso ajuda a categorizar seus registros de forma geral. Exemplos de Grupos:</p><ul><li><strong>Gastos</strong>: Todas as despesas, como contas e compras.</li><li><strong>Receitas</strong>: Entradas de dinheiro, como salário e rendas extras.</li><li><strong>Investimentos</strong>: Aplicações financeiras, como poupança, ações e fundos.</li></ul><p>Grupos ajudam você a responder perguntas como:</p><ul><li>Quanto estou gastando em comparação com o que ganho?</li><li>Quanto dinheiro está sendo destinado a investimentos?</li></ul><hr><h3 id="o-que-sao-categorias" tabindex="-1"><strong>O que são Categorias?</strong> <a class="header-anchor" href="#o-que-sao-categorias" aria-label="Permalink to &quot;**O que são Categorias?**&quot;">​</a></h3><p>Categorias são subdivisões dentro de um grupo. Elas permitem detalhar melhor suas transações. Por exemplo:</p><div style="display:flex;gap:10px;"><ul style="width:300px;"><li>Grupo: <b>Gastos</b></li><li>Categoria: <b>Casa</b></li><ul><li>Subcategoria: <b>Luz</b>, <b>Água</b>, <b>Internet</b></li></ul><li>Categoria: <b>Transporte</b></li><li>Categoria: <b>Lazer</b></li><li>Categoria: <b>Receitas</b></li></ul><table><tr><th>Grupo</th><th>Categoria</th><th>Subcategoria</th></tr><tr><td rowspan="5">Gastos</td><td rowspan="3">Casa</td><td>Luz</td></tr><tr><td>Água</td></tr><tr><td>Internet</td></tr><tr><td colspan="2">Transporte</td></tr><tr><td colspan="2">Lazer</td></tr></table></div><div style="display:flex;gap:10px;"><ul style="width:300px;"><li>Grupo: <b>Receitas</b></li><li>Categoria: <b>Salário</b></li><ul><li>Subcategoria: <b>1a quinzena</b>, <b>2a quinzena</b>, <b>Freelance</b>, <b>Rendimentos de aluguel</b></li></ul><li>Categoria: <b>Investimentos</b></li></ul><table><tr><th>Grupo</th><th>Categoria</th><th>Subcategoria</th></tr><tr><td rowspan="3">Receitas</td><td rowspan="2">Salário</td><td>1a quinzena</td></tr><tr><td>2a quinzena</td></tr><tr><td>Frelance</td><td>Rendimentos de aluguel</td></tr></table></div><div style="display:flex;gap:10px;"><ul style="width:300px;"><li>Grupo: <b>Investimentos</b></li><li>Categoria: <b>Ações</b></li><li>Categoria: <b>Poupança</b></li><li>Categoria: <b>Tesouro Direto</b></li></ul><table><tr><th>Grupo</th><th>Categoria</th></tr><tr><td rowspan="3">Investimentos</td><td>Ações</td></tr><tr><td>Poupança</td></tr><tr><td>Tesouro Direto</td></tr></table></div><p>Dessa forma, você pode entender com clareza:</p><ul><li>Para onde o dinheiro está indo dentro dos gastos.</li><li>De onde suas receitas estão vindo.</li><li>Quais tipos de investimentos estão recebendo mais aportes.</li></ul>',16)]))}const h=t(i,[["render",l]]);export{m as __pageData,h as default};
