import{_ as a,c as e,ag as s,o as i}from"./chunks/framework.-a8jReyC.js";const g=JSON.parse('{"title":"Solução de Problemas","description":"","frontmatter":{"title":"Solução de Problemas"},"headers":[],"relativePath":"troubleshooting.md","filePath":"troubleshooting.md"}'),r={name:"troubleshooting.md"};function t(l,o,n,u,c,m){return i(),e("div",null,o[0]||(o[0]=[s('<h1 id="solucao-de-problemas" tabindex="-1">Solução de Problemas <a class="header-anchor" href="#solucao-de-problemas" aria-label="Permalink to &quot;Solução de Problemas&quot;">​</a></h1><details class="details custom-block"><summary>Problema: Não consigo criar mais categorias</summary><ul><li><strong>Causa:</strong> Você pode ter atingido o limite do plano gratuito.</li><li><strong>Solução:</strong> Atualize para o plano pago para desbloquear categorias ilimitadas.</li></ul></details><details class="details custom-block"><summary>Problema: Não consigo acessar minha conta</summary><ul><li><strong>Causa:</strong> Credenciais incorretas ou conta bloqueada por segurança.</li><li><strong>Solução:</strong> Verifique seu e-mail e senha. Se necessário, utilize a opção &quot;Esqueci minha senha&quot; ou entre em contato com o suporte.</li></ul></details><details class="details custom-block"><summary>Problema: Dados não sincronizam com o servidor</summary><ul><li><strong>Causa:</strong> Problema de conexão com a internet ou erro no servidor.</li><li><strong>Solução:</strong> Verifique sua conexão e tente novamente. Caso o problema persista, aguarde alguns minutos ou entre em contato com o suporte.</li></ul></details><details class="details custom-block"><summary>Problema: Não consigo criar transações recorrentes</summary><ul><li><strong>Causa:</strong> Configuração incorreta do período de recorrência ou erro no sistema.</li><li><strong>Solução:</strong> Certifique-se de que todos os campos estão preenchidos corretamente. Se o erro continuar, reinicie o aplicativo ou atualize para a última versão.</li></ul></details><details class="details custom-block"><summary>Problema: A visualização de saldos está incorreta</summary><ul><li><strong>Causa:</strong> Grupos ou categorias utilizados no cálculo podem ter sido excluídos.</li><li><strong>Solução:</strong> Revise os saldos criados e ajuste ou exclua aqueles que usam grupos ou categorias inexistentes.</li></ul></details><details class="details custom-block"><summary>Problema: Modo offline não funciona</summary><ul><li><strong>Causa:</strong> Login inicial não realizado enquanto online ou problema de sincronização local.</li><li><strong>Solução:</strong> Faça login enquanto estiver conectado à internet para ativar o modo offline. Atualize para o plano premium para habilitar sincronização offline completa.</li></ul></details><details class="details custom-block"><summary>Problema: A tabela de categorias não carrega</summary><ul><li><strong>Causa:</strong> Cache do navegador corrompido ou erro no sistema.</li><li><strong>Solução:</strong> Limpe o cache do navegador e reinicie o aplicativo. Caso não resolva, atualize para a versão mais recente.</li></ul></details><details class="details custom-block"><summary>Problema: O aplicativo está lento</summary><ul><li><strong>Causa:</strong> Muitos dados armazenados ou dispositivo com pouca memória disponível.</li><li><strong>Solução:</strong> Exclua transações antigas ou exporte dados para reduzir o volume no aplicativo. Verifique o desempenho do dispositivo.</li></ul></details>',9)]))}const p=a(r,[["render",t]]);export{g as __pageData,p as default};
